mapper.select:
		select `cont_category_id`,`category_type`,`parent_category_id`,`category_name`,`category_code`,`sort`,`create_by`,`remark`,`gmt_create`,`gmt_modified` from cont_category

		  		   and cont_category_id = #{contCategoryId} 
		  		   and category_type = #{categoryType} 
		  		   and parent_category_id = #{parentCategoryId} 
		  		   and category_name = #{categoryName} 
		  		   and category_code = #{categoryCode} 
		  		   and sort = #{sort} 
		  		   and create_by = #{createBy} 
		  		   and remark = #{remark} 
		  		   and gmt_create = #{gmtCreate} 
		  		   and gmt_modified = #{gmtModified} 



                order by ${sort} ${order}


                order by cont_category_id desc



			limit #{offset}, #{limit}

select count(*) from cont_category

  		   and cont_category_id = #{contCategoryId} 
  		   and category_type = #{categoryType} 
  		   and parent_category_id = #{parentCategoryId} 
  		   and category_name = #{categoryName} 
  		   and category_code = #{categoryCode} 
  		   and sort = #{sort} 
  		   and create_by = #{createBy} 
  		   and remark = #{remark} 
  		   and gmt_create = #{gmtCreate} 
  		   and gmt_modified = #{gmtModified}

select #{langType} as lang_type,`cont_category_id`,`category_type`,`parent_category_id`,`category_name`,`category_code`,`sort`,`create_by`,`remark`,`gmt_create`,`gmt_modified` from cont_category

	 and cont_category_id = #{contCategoryId} 
	 and category_type = #{categoryType} 
	 and parent_category_id = #{parentCategoryId} 
	 and category_name = #{categoryName} 
	 and category_code = #{categoryCode} 
	 and sort = #{sort} 
	 and create_by = #{createBy} 
	 and remark = #{remark} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified} 



		order by ${sort} ${order}


		order by sort desc



	limit #{offset}, #{limit}
